args.content:
body {
    background-color: #C2B280; /* Dark, sandy background */
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    margin: 0;
    font-family: 'Courier New', Courier, monospace;
}

.game-container {
    width: 800px;
    height: 600px;
    position: relative;
    overflow: hidden;
    border: 2px solid #333;
}

.play-area {
    position: absolute;
    top: 50px;
    left: 50px;
    right: 50px;
    bottom: 100px;
    background-color: #F5F5DC; /* Light-colored play area */
    border: 2px solid #000;
}

#player {
    position: absolute;
    left: 20px;
    bottom: 0;
    width: 20px;
    height: 20px;
    background-color: #000; /* Small, black protagonist */
}

#exit {
    position: absolute;
    right: 20px;
    bottom: 0;
    width: 30px;
    height: 40px;
    background-color: #808080; /* Gray exit door */
    border: 2px solid #000;
}

.hud {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 50px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 10px;
}

.hud-left {
    display: flex;
}

.icon {
    width: 24px;
    height: 24px;
    border: 2px solid #000;
    margin-right: 10px;
}

.hud-center {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
}

.chevron-down {
    width: 0;
    height: 0;
    border-left: 30px solid transparent;
    border-right: 30px solid transparent;
    border-top: 30px solid #333; /* Dark downward chevron */
}


.controls {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 80px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 50px;
}

.d-pad {
    display: flex;
}

.key {
    width: 60px;
    height: 40px;
    border: 2px solid #000;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 24px;
    user-select: none;
}

.left-key {
    transform: scaleX(-1); /* Flip the arrow */
    margin-right: 10px;
}

.action-button .key {
    width: 50px;
    height: 50px;
    clip-path: polygon(50% 0%, 100% 38%, 82% 100%, 18% 100%, 0% 38%);
    background-color: transparent;
}

document.addEventListener('DOMContentLoaded', () => {
    const player = document.getElementById('player');
    const playArea = document.querySelector('.play-area');

    let playerX = 20;
    const playerSpeed = 10;
    const playAreaWidth = playArea.offsetWidth;
    const playerWidth = player.offsetWidth;

    player.style.left = `${playerX}px`;

    document.addEventListener('keydown', (event) => {
        switch (event.key) {
            case 'ArrowLeft':
                moveLeft();
                break;
            case 'ArrowRight':
                moveRight();
                break;
            case ' ':
                jump();
                break;
        }
    });

    function moveLeft() {
        playerX = Math.max(0, playerX - playerSpeed);
        player.style.left = `${playerX}px`;
    }

    function moveRight() {
        playerX = Math.min(playAreaWidth - playerWidth, playerX + playerSpeed);
        player.style.left = `${playerX}px`;
    }

    function jump() {
        // A simple visual indication of a jump
        if (!player.classList.contains('jumping')) {
            player.classList.add('jumping');
            player.style.bottom = '40px';
            setTimeout(() => {
                player.style.bottom = '0px';
                setTimeout(() => {
                    player.classList.remove('jumping');
                }, 300);
            }, 300);
        }
    }
});
